mapper.select:
SELECT COUNT(*) FROM ASSIGNMENT A
JOIN ASSIGNMENT_GROUP G USING(GR_NO)

WHERE AS_STATUS = 1
AND ST_NO = #{stNo}

AND GR_NO = #{grNo}

ORDER BY AS_NO DESC

SELECT AS_NO, GR_NO, AS_NAME, AS_CONTENTS, AS_INSERTDATE, AS_DEADLINE, AS_STATUS FROM ASSIGNMENT A JOIN ASSIGNMENT_GROUP G USING(GR_NO)
WHERE AS_STATUS = 1 AND ST_NO = #{stNo}
AND GR_NO = #{grNo}
ORDER BY AS_NO DESC

SELECT * FROM ASSIGNMENT_GROUP
WHERE GR_NO = #{grNo}
AND GR_STATUS = 1

SELECT * FROM ASSIGNMENT
WHERE AS_NO = #{asNo}
AND AS_STATUS = 1

mapper.update:
UPDATE ASSIGNMENT_GROUP SET
GR_NAME = #{grName}, GR_INFO = #{grInfo}, GR_COLOR = #{grColor}
WHERE GR_NO = #{grNo}
AND GR_STATUS = 1

mapper.delete:
DELETE FROM ASSIGN
WHERE GR_NO = #{grNo}
AND MB_NO = #{mbNo}

mapper.update:
UPDATE ASSIGNMENT SET
AS_NAME = #{asName}, AS_CONTENTS = #{asContents}, AS_DEADLINE = #{asDeadLine}
WHERE AS_NO = #{asNo}

UPDATE ASSIGNMENT SET
AS_STATUS = 0
WHERE AS_NO = #{asNo}
